def day2(v):
    def format_sheet(v):
        return [[int(i) for i in line.split()] for line in v.split('\n')]
        
    def get_mm(line):
        if len(line) == 0: return 0
        n = x = line[0]
        for i in line:
            if i > x:
                x = i
            if i < n:
                n = i
        return x - n

    s = format_sheet(v)
    return reduce(lambda x, y: x + y, map(get_mm, s))


