def spiral_matrix(n):
    m = [[0] * n for i in range(n)]
    dx, dy = [0, 1, 0, -1], [1, 0, -1, 0]
    x, y, c = 0, -1, n ** 2 
    for i in range(n + n - 1):
        for j in range((n + n - i) // 2):
            x += dx[i % 4]
            y += dy[i % 4]
            m[x][y] = c
            c -= 1
    return m

def find_pos(m, n, size):
    # lol x y are reverse
    x = y = size - 1
    while x > 0:
        x -= 1
        if m[x][y] == n:
            return x, y
    while y > 0:
        y -= 1
        if m[x][y] == n:
            return x, y
    while x < size:
        x += 1
        if m[x][y] == n:
            return x, y
    while y < size:
        y += 1
        if m[x][y] == n:
            return x, y


def day3(n):
    # 1 - 2 - 4 - 6 - ...
    size = ceil(sqrt(n))
    size = size if size % 2 == 1 else size + 1
    m = spiral_matrix(size)
    y, x = find_pos(m, n, size)
    center = size // 2
    steps = 0
    while x != center or y != center:
        # move one step closer
        if x > center:
            x -= 1
        elif x < center:
            x += 1
        elif y > center:
            y -= 1
        elif y < center:
            y += 1
        steps += 1
    
    return m, x, y, steps


